# coding: utf-8from sqlalchemy import Column, DateTime, Integer, Stringfrom sqlalchemy.schema import FetchedValuefrom application import db, appclass Balance_Log(db.Model):    __tablename__ = 'balance_log'    id = db.Column(Integer, primary_key=True)    merchant_id = db.Column(String(60), nullable=False, server_default=FetchedValue())    createtime = db.Column(DateTime, nullable=False, server_default=FetchedValue())    updatetime = db.Column(DateTime, nullable=False, server_default=FetchedValue())    status = db.Column(DateTime, nullable=False, server_default=FetchedValue())    balance = db.Column(DateTime, nullable=False, server_default=FetchedValue())    operating = db.Column(DateTime, nullable=False, server_default=FetchedValue())    total_balance = db.Column(DateTime, nullable=False, server_default=FetchedValue())    freeze_balance = db.Column(DateTime, nullable=False, server_default=FetchedValue())    receipt_qrcode = db.Column(DateTime, nullable=False, server_default=FetchedValue())    @property    def Operating_Desc(self):        return app.config['OPER_MAPPING'][str(self.operating)]    @property    def Status_Desc(self):        return app.config['OPER_STATUS_MAPPING'][str(self.status)]